  imgSrc = '';

  let complimentText;

  const num = Math.floor(Math.random() * (4 - 1) + 1);
  
  switch(num) {
    case 1:
      complimentText = knope.getCompliment('Shé');
      break;
    case 2:
      complimentText = knope.getCompliment('Shé', 3);
      break;
    case 3:
      complimentText = knope.getOfficialCompliment('Shé', 3, false);
      break;
  }

